import { React, useEffect, useState } from "react";
import Input from "../../UI/Input";
import classes from "./MealsItemForm.module.css";
import Authcontext from "../../../Context/auth-context";

const MealsItemForm = (props) => {
  const [isAmount, setisAmount] = useState(0);
  const [isBtnclick, setisBtnclick] = useEffect(false);

  const onAdd = (e) => {
    console.log(e);
    setisAmount(e);
    console.log(isAmount);
  };

  useEffect(() => {
    console.log(isAmount);
  }, [isAmount]);

  const Addbtn = (e) => {
    if (e) {
      setisBtnclick(true);
    }
  };

  return (
    <React.Fragment>
      <Authcontext.Provider
        value={{
          isAmount: isAmount,
        }}
      >
        <form className={classes.form}>
          <Input
            onAdd={onAdd}
            min="1"
            max="5"
            label="Amount"
            input={{
              id: "amount_" + props.id,
              type: "numbeer",
              min: "1",
              max: "5",
              step: "1",
              defaultValue: "1",
            }}
          ></Input>

          <button className={classes.formbtn} onClick={Addbtn}>
            + Add
          </button>
        </form>
      </Authcontext.Provider>
    </React.Fragment>
  );
};

export default MealsItemForm;
