import classes from "./Cart.module.css";
import ReactDOM from "react-dom";
import React, { Fragment, useEffect, useState } from "react";
import Modal from "../UI/Modal";

const Cart = (props) => {
  const [CloseValue, SetCloseValue] = useState(false);
  const [OrderValue, SetOrderValue] = useState(false);

  const cartitem = (
    <ul className={classes.cartitems}>
      {[{ id: "m1", name: "shushi", amount: 2, price: 13.13 }].map((item) => (
        <li>{item.name}</li>
      ))}
    </ul>
  );

  const closebtn = (e) => {
    if (e) {
      SetCloseValue(true);
      props.onclose(CloseValue);
    }
  };

  return (
    <div>
      {cartitem}
      <div className={classes.total}>
        <span>Total Amount</span>
        <span>35.62</span>
      </div>
      <div className={classes.actions}>
        <button className={classes.closebtn} onClick={closebtn}>
          Close
        </button>
        <button className={classes.orderbtn}>Order</button>
      </div>
      <Modal></Modal>
    </div>
  );
};

export default Cart;
