import { useState } from "react";
import Input from "../../UI/Input";
import classes from "./MealsItemForm.module.css";

const MealsItemForm = (props) => {
  const [isAmount, setisAmount] = useState(0);

  const addbtn = (e) => {
    if (e) {
      props.onAddbtn(isAmount);
    }
  };

  const onAddbtn = (e) => {
    setisAmount(e);
  };
  return (
    <form className={classes.form}>
      <Input
        onAddbtn={onAddbtn}
        min="1"
        max="5"
        label="Amount"
        input={{
          id: "amount_" + props.id,
          type: "numbeer",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      ></Input>
      <button className={classes.formbtn} onClick={addbtn}>
        + Add
      </button>
    </form>
  );
};

export default MealsItemForm;
