import classes from "./Input.module.css";

const Input = (props) => {
  const [isAmount, setisAmount] = useState(1);

  const inputvalue = (e) => {
    setisAmount(e);
    props.onAddbtn(isAmount);
  };
  return (
    <div className={classes.inputform}>
      <label htmlFor={props.input.id} className={classes.inputlabel}>
        {props.label}
      </label>
      <input
        {...props.input}
        className={classes.input}
        onChange={inputvalue}
      ></input>
    </div>
  );
};
export default Input;
