import classes from "./Cart.module.css";
import ReactDOM from "react-dom";
import { Fragment, useEffect } from "react";

const CartModal = () => {
  return <div className={classes.cart}></div>;
};

const Cart = (props) => {
const [BtnClick, setBtnClick] = useEffect(false);

setBtnClick(props.CartClick);

  return (
    <Fragment>
      {ReactDOM.createPortal(
        <CartModal />,
        document.getElementById("cartmodal")
      )}
    </Fragment>
  );
};

export default Cart;
