import React, { Fragment, useContext, useEffect, useState } from "react";
import Cart from "../Cart/Cart";
import classes from "./Header.module.css";
import mealsimg from "../../assets/meals.jpg";
import MealsSummary from "../Meals/MealsSummary";
import Modal from "../UI/Modal";
import Authcontext from "../../Context/auth-context";

const Header = (props) => {
  const [isItem, setIsitem] = useState(0);

  const closebtn = (e) => {
    if (e) {
      props.onmodal(true);
    }
  };

  const ctx = useContext(Authcontext);
  console.log(ctx.isAmount);
  useEffect(() => {
    setIsitem(ctx.isAmount);
  }, [isItem]);
  // setIsitem(ctx);
  console.log(isItem);

  return (
    <React.Fragment>
      <header className={classes.header}>
        <h1>FoodCafe</h1>
        <button className={classes.btn} onClick={closebtn}>
          <span className={classes["cart-imoji"]}>ðŸ›’</span>
          <span>Your Cart</span>
          <span className={classes.count}>{isItem}</span>
        </button>
      </header>
      <div className={classes["main-img"]}>
        <img src={mealsimg} />
      </div>
    </React.Fragment>
  );
};

export default Header;
